import React from 'react';
import './App.css';
// import 'pretty-checkbox';
class CodeCalender extends React.Component {
    constructor(){
        super()
    this.state = {
        result: {},
        ongoing: [],
        upcoming: [],
        hacker: true,
        rank: true,
        code: true,
        cforces: true,
    }
    };

    //    componentDidMount=()=>{
    //        fetch('http://contesttrackerapi.herokuapp.com/android/').then((res)=>{
    //            return res.json();
    //        }).then((data)=>{
    //         console.log(data)
    //        })
    //    }
    componentDidMount = () => {
        this.getContestData();
    }
getContestData = () => {
    fetch('http://contesttrackerapi.herokuapp.com/android/').then((res) => {
        return res.json();
    }).then((data) => {
        console.log(data.result.ongoing);
        this.setState({
            ongoing: data.result.ongoing
        })
        console.log(data.result.upcoming);
        this.setState({
            upcoming: data.result.upcoming
        })
    })
}

render() {
    return (
        <div>
            <div className="mainbtn">
                <div className="custom-control custom-checkbox">
                    <input type="checkbox" className="custom-control-input" id="customCheck1" onChange={this.handleContest} defaultChecked="true"/>
                    <label class="custom-control-label" for="customCheck1">HACKEREARTH</label>
                </div>
                <div className="custom-control custom-checkbox">
                    <input type="checkbox" className="custom-control-input" id="customCheck2" onChange={this.handleContest} defaultChecked="true"/>
                    <label class="custom-control-label" for="customCheck2">HACKERRANK</label>
                </div>
                <div className="custom-control custom-checkbox">
                    <input type="checkbox" className="custom-control-input" id="customCheck3" onChange={this.handleContest} defaultChecked="true"/>
                    <label class="custom-control-label" for="customCheck3">CODECHEF</label>
                </div>
                <div className="custom-control custom-checkbox">
                    <input type="checkbox" className="custom-control-input" id="customCheck4" onChange={this.handleContest} defaultChecked="true"/>
                    <label class="custom-control-label" for="customCheck4">CODEFORCES</label>
                </div>
            </div>
            <div className="hack">
                <div className="upcmng">
                    <h2>ONGOING CONTEST</h2>
                </div>

                {this.state.ongoing.map((data, index) => {

                    return (
                        <div className="onBox" key={index}>
                            <button type="button" data-toggle="collapse" data-target={"#" + index} className="btn btn-outline-success button1 "><h6>Name:{data.Name}</h6> </button>
                            <div id={index} className="collapse">
                                <p>Name:{data.Name}</p>
                                <p>EndTime:{data.EndTime}</p>
                                <p>Platform:{data.Platform}</p>
                                {data.challenge_type ? (<p>challenge_type:{data.challenge_type}</p>) : null}
                                <p>url:<a target='blank' href={data.url}>{data.url}</a></p>
                            </div>
                        </div>
                    )
                })}

            </div>
            <div className="hack">
                <div className="upcmng">
                    <h2>UPCOMING CONTEST</h2>
                </div>
                {this.state.upcoming.map((data, index) => (
                    <div className="oneBox" key={index}>
                        <p>Duration:{data.Duration}</p>
                        <p>EndTime:{data.EndTime}</p>
                        <p>Name:{data.Name}</p>
                        <p>Platform:{data.Platform}</p>
                        <p>StartTime:{data.StartTime}</p>
                        <p>url:{data.url}</p>
                    </div>
                ))}
            </div>

        </div>
    );
}
}
export default CodeCalender;
